import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Invoices = () => {
  const [invoices, setInvoices] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/invoices', { withCredentials: true })
      .then(response => setInvoices(response.data))
      .catch(error => console.error('Error fetching invoices:', error));
  }, []);

  const triggerAutomation = () => {
    axios.post('http://localhost:5000/zapier/trigger', {}, { withCredentials: true })
      .then(response => console.log(response.data))
      .catch(error => console.error('Error triggering automation:', error));
  };

  const handleLogout = async () => {
    try {
      await axios.get('http://localhost:5000/auth/logout', { withCredentials: true });
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div>
      <h1>Due Invoices</h1>
      <ul>
        {invoices.map(invoice => (
          <li key={invoice.id}>
            {invoice.recipient}: ${invoice.amount} due on {invoice.dueDate}
          </li>
        ))}
      </ul>
      <button onClick={triggerAutomation}>Trigger Automation</button>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Invoices;
